package operaters;

public class arithmetic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10; int b=20; 
//addition
int c=a+b;
System.out.println(c);
//subtraction
int c1=a-b;
System.out.println(c1);
//multiplication
int c2=a*b;
System.out.println(c2);
//division 
int c3=b/a;
System.out.println(c3);
//modulus
int c4=b%a;
System.out.println(c4);
	}

}
